import React, {useState} from 'react'
import { Link, useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';
import axios from 'axios';
import { login } from '../service/UserService';

import loginImage from '../asset/loginprofile.jpg';
import { logDOM } from '@testing-library/react';

export const Login = () => {
  let navigate = useNavigate();

  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [form, setForm] = useState({
    email: "",
    password: ""
  })
  const [userData, setUserData] = useState();

  const renderErrorMessage = (name) =>
  name === errorMessages.name && (
    <div className="error">{errorMessages.message}</div>
  );

  const handleChange = (e) => {
    const nextFormState = {
      ...form,
      [e.target.name]: [e.target.value],
    };
    setForm(nextFormState);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsLoading(true);
    var user = JSON.stringify({
      "email": `${form.email}`,
      "password": `${form.password}`
    });

    console.log("User: " + user)

    // var myHeaders = new Headers();
    // myHeaders.append("Accept-Charset", "UTF-8");
    // myHeaders.append('Accept', 'application/json');
    // myHeaders.append('Content-Type', 'application/json');

    // var requestOptions = {
    //   method: 'POST',
    //   headers: myHeaders,
    //   body: user,
    //   redirect: 'follow'
    // };

    // const fetchLogin = fetch("http://localhost:8080/UserProfileClient/api-login", requestOptions);

    // fetchLogin.then(response => console.log(response))
    //   .then(result => console.log("result: " + result))
    //   .catch(error => console.log('error', error));

    // var config = {
    //   method: 'post',
    //   url: 'http://localhost:8080/UserProfileClient/api-login',
    //   headers: { 
    //     'Accept': 'application/json',
    //     'Content-Type': 'application/json', 
    //     'Accept-Charset': 'UTF-8', 
    //     //'Cookie': 'JSESSIONID=751E4FFF1CF8B704C851CC60EA54044C'
    //   },
    //   data : user
    // };

    // axios(config)
    //   .then(function (response) {
    //     console.log(response.data);
    //   })
    //   .catch(function (error) {
    //     console.log(error);
    //   });


    login(user).then(res =>{
      setIsLoading(false);
      //navigate("/thong-tin-ca-nhan");
      console.log("data: " + res.data);
      alert("Dang nhap thanh cong!");
    });

  }

  return (
    <div className="form-wrapper">
      <div className="form">
        <div className="form-login__header">
            <h1>Dang nhap</h1>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="input-container">
            <label>Email: </label>
            <input type="email" name="email" value={form.email} onChange={handleChange} />
            {renderErrorMessage("email")}
          </div>
          <div className="input-container">
            <label>Mat khau </label>
            <input type="password" name="password" value={form.password} onChange={handleChange} />
            {renderErrorMessage("password")}
          </div>
          <div className="button-container">
            <input type="submit" />
          </div>
          <span>Chua co tai khoan. <Link to="/dang-ky">Dang ky ngay!</Link></span>
        </form>
      </div>
    </div>
  )
}
