import React, {useState} from 'react'
import { Link, useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';
import axios from 'axios';
import { toast } from 'react-toastify';

import { api } from '../config/axiosConfig';
import loader from '../asset/Spinner-1s-64px.svg';

export const Login = () => {
  let navigate = useNavigate();

  const [errorMessages, setErrorMessages] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [form, setForm] = useState({
    email: "",
    password: ""
  })

  const renderErrorMessage = (name) =>
  name === errorMessages.name && (
    <div className="error">{errorMessages.message}</div>
  );

  const handleChange = (e) => {
    const nextFormState = {
      ...form,
      [e.target.name]: [e.target.value],
    };
    setForm(nextFormState);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsLoading(true);

    var user = JSON.stringify({
      "email": `${form.email}`,
      "password": `${form.password}`
    })

    var config = {
      method: 'post',
      url: 'http://localhost:8080/Client/api-login',
      headers: { 
        'Accept-Charset': 'UTF-8', 
        'Content-Type': 'application/json', 
      },
      data : user
    };

    //api.post("/api-login", user)
    axios(config)
      .then((res) => {
        if(res){
          console.log("res: " + JSON.stringify(res.data.user));
          let userLS = {
            id: res.data.user.id,
            name: res.data.user.name,
            email: res.data.user.email,
            password: res.data.user.password,
            address: res.data.user.address,
            phone: res.data.user.phone
          }

          console.log(userLS)
          localStorage.setItem('user', JSON.stringify(userLS));
          Cookies.set('cookie_id', res.headers['x-access-token']);
          console.log("cookie: " + res.headers);

          setIsLoading(false);
          navigate("/thong-tin-ca-nhan")
          toast.success('Dang nhap thanh cong!', {
            position: "top-right",
            autoClose: 3000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
          })
        }
      })
      .catch(err => {
        toast.error(err.message, {
          position: "top-right",
          autoClose: 3000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "light",
        })
      });
  }

  return (
    <div className="form-wrapper">
      <div className="form">
        <div className="form-login__header">
            <h1>Dang nhap</h1>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="input-container">
            <label>Email: </label>
            <input type="email" name="email" value={form.email} onChange={handleChange} />
            {renderErrorMessage("email")}
          </div>
          <div className="input-container">
            <label>Mat khau </label>
            <input type="password" name="password" value={form.password} onChange={handleChange} />
            {renderErrorMessage("password")}
          </div>
          <div className="button-container">
            <button type="submit" ><img src={loader} alt="loader" /> Dang nhap</button>
          </div>
          <span>Chua co tai khoan. <Link to="/dang-ky">Dang ky ngay!</Link></span>
        </form>
      </div>
    </div>
  )
}
