import React, { useCallback, useEffect, useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'

export const Navbar = () => {

  const navigate = useNavigate();

  const [isLogedIn, setIsLogedIn] = useState(false);


  const logout = useCallback(()=>{
    localStorage.clear();
    setTimeout(() => {
      navigate("/");
    }, [1000])
  }, [])

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if(user){
        setIsLogedIn(true)
    }

  }, [isLogedIn, logout]);
  return (
    <div className="navbar container">
      <Link to="/" className="logo">QL Thong Tin Ca Nhan</Link>
      {
        isLogedIn
        ?
        <Link to="/dang-nhap" className="login-button">Dang nhap</Link>
        :
        <button onClick={logout} className="login-button">Dang xuat</button>
      }
    </div>
  )
}
