import React, {useState} from 'react'
import { Link, useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';
import { toast } from 'react-toastify';

import { api } from '../config/axiosConfig';
import loader from '../asset/Spinner-1s-64px.svg';

export const Login = () => {
  let navigate = useNavigate();

  const [errorMessages, setErrorMessages] = useState({});
  const [isError, setIsError] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [form, setForm] = useState({
    email: "",
    password: ""
  })

  const renderErrorMessage = (name) =>
  name === errorMessages.name && (
    <div className="error">{errorMessages.message}</div>
  );

  const handleChange = (e) => {
    setIsError(false);
    const nextFormState = {
      ...form,
      [e.target.name]: [e.target.value],
    };
    setForm(nextFormState);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsLoading(true);
    setIsError(false);

    var user = JSON.stringify({
      "email": `${form.email}`,
      "password": `${form.password}`
    })

    api.post("/api-login", user)
      .then((res) => {
        console.log(res.headers);
        console.log("re: " + JSON.stringify(res));
        if(res.data.user){
          let userLS = {
            id: res.data.user.id,
            name: res.data.user.name,
            email: res.data.user.email,
            password: res.data.user.password,
            address: res.data.user.address,
            phone: res.data.user.phone
          }
          localStorage.setItem('user', JSON.stringify(userLS));
          //Cookies.set('cookie_id', res.headers['x-access-token']);

          navigate(`/thong-tin-ca-nhan`);
          setIsLoading(false);
          toast.success('Đăng nhập thành công!', {
            position: "top-right",
            autoClose: 3000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
          })
        }else{
          setIsError(true);
          setIsLoading(false);
          toast.error(res.data.errMessage, {
            position: "top-right",
            autoClose: 3000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
          })
        }
      })
      .catch(err => {
        setIsLoading(false);
        console.log("err" + err)
      });
  }

  return (
    <div className="form-wrapper">
      <div className="form">
        <div className="form-login__header">
            <h1>Đăng nhập</h1>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="input-container">
            <label>Email: </label>
            <input type="email" name="email" className={isError?"err":null} value={form.email} onChange={handleChange} />
            {renderErrorMessage("email")}
          </div>
          <div className="input-container">
            <label>Mật khẩu: </label>
            <input type="password" name="password" className={isError?"err":null} value={form.password} onChange={handleChange} />
            {renderErrorMessage("password")}
          </div>
          <div className="button-container">
            <button type="submit" >{ isLoading && <img src={loader} alt="loader" />} Đăng nhập</button>
          </div>
          <span>Chưa có tài khoản. <Link to="/dang-ky"> Đăng ký ngay!</Link></span>
        </form>
      </div>
    </div>
  )
}
