import React, {useState} from 'react'
import { Link, useNavigate } from 'react-router-dom';

import signupImage from '../asset/signupprofile.jpg';

export const Register = () => {
  let navigate = useNavigate();

  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    passwordAgain: "",
    address: "",
    phone: ""
  })

  const renderErrorMessage = (name) =>
  name === errorMessages.name && (
    <div className="error">{errorMessages.message}</div>
  );

  const handleChange = (e) => {
    const nextFormState = {
      ...form,
      [e.target.name]: [e.target.value],
    };
    setForm(nextFormState);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    var myHeaders = new Headers();
    myHeaders.append("Accept-Charset", "UTF-8");
    myHeaders.append("Content-Type", "application/json");
    myHeaders.append("Access-Control-Allow-Origin", "*");

    var raw = JSON.stringify({
      "name": "Lý Bach",
      "email": "lybach@gmail.com",
      "password": "10271998",
      "address": "09 Tô Vĩnh Diện - Tân Hòa - Đông Hòa - Dĩ An - Bình Dương",
      "phone": "0589598151"
    });

    console.log("User: " + raw)
    var requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: raw,
      redirect: 'follow'
    };

    fetch("http://localhost:8080/UserProfileClient/api-register", requestOptions)
      .then(response => response.text())
      .then(result => console.log(result))
      .catch(error => console.log('error', error));

    navigate("/dang-nhap")
  }

  return (
    <div className="form-wrapper">
        <div className="form">
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label>Ho Ten: </label>
              <input type="text" name="name" value={form.name} onChange={handleChange} />
              {renderErrorMessage("name")}
            </div>
            <div className="input-container">
              <label>Email: </label>
              <input type="email" name="email" value={form.email} onChange={handleChange} />
              {renderErrorMessage("email")}
            </div>
            <div className="input-container">
              <label>Mat khau: </label>
              <input type="password" name="password" value={form.password} onChange={handleChange} />
              {renderErrorMessage("password")}
            </div>
            <div className="input-container">
              <label>Mat khau: </label>
              <input type="password" name="passwordAgain" value={form.passwordAgain} onChange={handleChange} />
              {renderErrorMessage("passwordAgain")}
            </div>
            <div className="input-container">
              <label>Dia chi </label>
              <input type="text" name="address" value={form.address} onChange={handleChange} />
              {renderErrorMessage("address")}
            </div>
            <div className="input-container">
              <label>So dien thoai: </label>
              <input type="text" name="phone" value={form.phone} onChange={handleChange} />
              {renderErrorMessage("phone")}
            </div>
            <div className="button-container">
              <input type="submit" />
            </div>
            <span>Da co tai khoan. <Link to="/dang-nhap">Dang nhap ngay!</Link></span>
          </form>
        </div>
    </div>
  )
}
