import './App.css';
import React, { useEffect, useState } from 'react';
import 'react-toastify/dist/ReactToastify.css';
import { Navbar } from './component/Navbar';
import { Routes, Route, Navigate } from 'react-router-dom';

import { Home } from './component/Home';
import { Login } from './component/Login';
import { Register } from './component/Register';
import { Profile } from './component/Profile';
import { ToastContainer } from 'react-toastify';
import { PrivateRoute } from './component/PrivateRoute';
import AuthVerify from './common/auth-verify';
import { api } from './config/axiosConfig';

function App() {

  const autoLogout = () =>{
    localStorage.clear('user');
    Navigate("/");
  }

  useEffect(() => {
    api.get("/api-first-access")
    .then(res => {
      console.log("trc res: " + JSON.stringify(res));
      if(res.data.user){
        let userLS = {
          id: res.data.user.id,
          name: res.data.user.name,
          email: res.data.user.email,
          password: res.data.user.password,
          address: res.data.user.address,
          phone: res.data.user.phone
        }

        localStorage.setItem('user', JSON.stringify(userLS));
        //setIsLoading(false);
        console.log("Đăng nhập thành công!");

      }else{
        // setIsLoading(false);
        // navigate("/");
        console.log("Phiên đăng nhập hết hạn!");
      }
    })
    .catch(err => {
      //setIsLoading(false);
      console.log("err" + err)
    });
  })

  return (
    <>
      <Navbar />
      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
        />
        <Routes>
          <Route exac path="/" element={<Home />} />
          <Route exac path="dang-nhap" element={<Login />} />
          <Route exac path="dang-ky" element={<Register />} />
          <Route path="thong-tin-ca-nhan" element={
                  <PrivateRoute> 
                    <Profile /> 
                  </PrivateRoute>} />
          <Route path="*" element={<p>There's nothing here: 404!</p>} />
        </Routes>
      <ToastContainer />
      {/* <AuthVerify logOut={()=> autoLogout()} /> */}
    </>
  );
}

export default App;
